import React, { useEffect, useState } from 'react'

const Daily = () => {
    const [daily , setDaily] = useState([]);
    useEffect(()=>{
        fetch("http://localhost:3004/dailly")
        .then(response => response.json())
        .then(data => {
          // Handle the response data
          console.log(data);
          setDaily(data)
        })

    },[])
    const desert = daily.filter (obj => obj.category == "dessert");
    const appetizers = daily.filter (obj => obj.category == "appetizers");
    const main = daily.filter (obj => obj.category == "main");
  return (
    <div>
      <h1>Desert</h1>
      { desert.map(obj=> (
        <div>{obj.title}: {obj.description}</div>
      )) }
    </div>
    // Εδω ή θα φτιαξω καινουργιο Component που θα του περναω σαν prop το daily  ή θα το σεταρω εδω
  )
}

export default Daily
