import React, {useState, useEffect} from "react";
import './Happyhour.css'
import HappyComponent from "./HappyComponent";

const Happyhour = () => {
    const [offers ,setOffers] = useState([]); // state will be used to store the fetched data.

    useEffect(() => {
        // Fetch the data from the API endpoint
        fetch("http://localhost:3004/happyhour")
          .then((response) => response.json())
          .then((data) => {
            // Handle the response data
            console.log(data);
            setOffers(data);
          });
      }, []);

      const handleSortDescending = () => {
        const sortedArray = [...offers].sort(
          (
            a,
            b
            // creates a new sorted array based on the offers state, sorting the objects by their title property in ascending order.
          ) => (a.title < b.title ? 1 : -1)
        );
        setOffers(sortedArray); //stores the sorted array on offers state
      };
      const sortedAscArray = [...offers].sort(
        (
          a,
          b
          // creates a new sorted array based on the offers state, sorting the objects by their title property in ascending order.
        ) => (a.title > b.title ? 1 : -1)
      );

  return (
    <>
    <h1 className="text-center mt-4">Happy Hour</h1>
    <h4 className="text-center mt-4">`16:00 - 18:00`</h4>
    <div className="container happyhour">
    <HappyComponent
          offers={sortedAscArray}
        //   handleSortAscending={() => handleSortAscending(offers, setOffers)}
           handleSortDescending={handleSortDescending}
        />
    </div>
    </>
  )
}

export default Happyhour
