import React from 'react'

const DailyComponent = ({daily}) => {
    const desert = daily.filter (obj => obj.category == "dessert");
    const appetizers = daily.filter (obj => obj.category == "appetizers");
    const main = daily.filter (obj => obj.category == "main");
  return (
    <>
    <div><div class="card" style="width: 18rem;">
    <div class="card-body">
      <h5 class="card-title">Card title</h5>
      <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
      <a href="#" class="btn btn-primary">Go somewhere</a>
    </div>
  </div></div>
  <div class="card" >
       <div class="card-body">
       <h5 class="card-title">Desert</h5>
      { desert.map(obj=> (
         <p class="card-text">{obj.title}: {obj.description}</p>
      )) }
       <h5 class="card-title">Appetizers</h5>
      { appetizers.map(obj=> (
         <p class="card-text">{obj.title}: {obj.description}</p>
      )) }
       <h5 class="card-title">Main Entrees</h5>
      {main.map(obj=> (
         <p class="card-text">{obj.title}: {obj.description}</p>
      )) }
         </div>
     </div>
  </>
  )
}

export default DailyComponent
