import React, {useState, useEffect} from "react";


    const HappyComponent = ({ offers, handleSortDescending }) => {
        const [sortDescending, setSortDescending] = useState(false);
      
        const handleSortToggle = () => {
          setSortDescending((prevSortDescending) => !prevSortDescending);
        };
      
        const sortedOffers = sortDescending
          ? [...offers].sort((a, b) => (a.title < b.title ? 1 : -1))
          : offers;
  return (
<table class="table">
  <thead>
    <tr>
      <th scope="col"><button type='button' onClick={handleSortToggle}>Title</button></th>
      <th scope="col"><button>Description</button></th>
      <th scope="col"><button>Price</button></th>
    </tr>
  </thead>
  <tbody>
    {sortedOffers.map(offer => 
         <tr>
        <td>{offer.title}</td>
        <td>{offer.description}</td>
        <td>{offer.price}</td>
      </tr>
        )}
    
  </tbody>
</table>
  )
}

export default HappyComponent
