import DailyComponent from './DailyComponent';

const Daily = () => {
    const [daily , setDaily] = useState([]);
    useEffect(()=>{
        fetch("http://localhost:3004/dailly")
        .then(response => response.json())
        .then(data => {
          // Handle the response data
          console.log(data);
          setDaily(data)
        })

    },[])

