import React, {useState, useEffect} from "react";

const HappyComponent = ({offers}) => {
    const [handleSortDescending ,setHandleSortDescending] = useState(false)
    const [handleAscending ,setHandleSortAscending] = useState(false)
    const handleSortToggle =() =>{
        // {!handleSortDescending ? setHandleSortDescending(true): setHandleSortDescending(false)}
        if(handleSortDescending){
            setHandleSortDescending(false)
            setHandleSortAscending(true)
        }else{
            setHandleSortDescending(true)
            setHandleSortAscending(FALSE)
        }
    }

  return (
<table class="table">
  <thead>
    <tr>
      <th scope="col"><button type='button' onClick={handleSortToggle}>Title</button></th>
      <th scope="col"><button>Description</button></th>
      <th scope="col"><button>Price</button></th>
    </tr>
  </thead>
  <tbody>
    {offers.map(offer => 
         <tr>
        <td>{offer.title}</td>
        <td>{offer.description}</td>
        <td>{offer.price}</td>
      </tr>
        )}
    
  </tbody>
</table>
  )
}

export default HappyComponent
