import React, {useState, useEffect} from "react";

const HappyComponent = ({offers  }) => {

    const [handleSortAscending, setSortAscending] = useState(true);

  return (
<table class="table">
  <thead>
    <tr>
      <th scope="col"><button type='button' onClick={!handleSortAscending?handleSortAscending : setSortAscending(prevSortAscending => !prevSortAscending)}>Title</button></th>
      <th scope="col"><button>Description</button></th>
      <th scope="col"><button>Price</button></th>
    </tr>
  </thead>
  <tbody>
    {offers.map(offer => 
         <tr>
        <td>{offer.title}</td>
        <td>{offer.description}</td>
        <td>{offer.price}</td>
      </tr>
        )}
    
  </tbody>
</table>
  )
}

export default HappyComponent
