import React from 'react'

const HappyComponent = ({offers}) => {
    const handleSortClick = () => {
        const sortedArray = [...offers].sort(
          (
            a,
            b
            // creates a new sorted array based on the specials state, sorting the objects by their title property in ascending order.
          ) => (a.title > b.title ? 1 : -1)
        );
        setOffers(sortedArray);
}
  return (
<table class="table">
  <thead>
    <tr>
      <th scope="col"><button onClick={handleSortClick}>Title</button></th>
      <th scope="col"><button>Description</button></th>
      <th scope="col"><button>Price</button></th>
    </tr>
  </thead>
  <tbody>
    {offers.map(offer => 
         <tr>
        <td>{offer.title}</td>
        <td>{offer.description}</td>
        <td>{offer.price}</td>
      </tr>
        )}
    
  </tbody>
</table>
  )
}

export default HappyComponent
