import React from 'react'

const SpecialComponent = ({specials}) => {
    const [sortedSpecials, setSortedSpecials] = useState([...specials]);

    const strAscending =[...specials].sort((a, b) =>
    a.title > b.title ? 1 : -1,
  );
  console.log(strAscending)
  return (
    <div className="specialCard shadow-lg">
         <button type="button" className="btn" onClick={strAscending}>
            Filter based the name
          </button>
         {sortedSpecials.map((special) =>(
            <div>
          <h4  data-toggle="tooltip" data-placement="top" title={special.title}>{special.title}</h4> 
          <p>{special.description}</p>
          {/* sto tooltip na valw category description */}
          </div>
        ))}
    </div>
  )
}

export default SpecialComponent
