import React, {useState, useEffect} from "react";

const HappyComponent = ({offers ,setOffers }) => {

    const sortedDescendingArray = [...offers].sort((a, b) => (a.title < b.title ? 1 : -1));
    const sortedAscendingArray = [...offers].sort((a, b) => (a.title > b.title ? 1 : -1));

const handleSort = (offers, setOffers) => {
    const sortedArray = [...offers].sort((a, b) => (a.title > b.title ? 1 : -1));
    setOffers(sortedArray);
  };

  return (
<table class="table">
  <thead>
    <tr>
      <th scope="col"><button type='button' onClick={handleSortToggle}>Title</button></th>
      <th scope="col"><button>Description</button></th>
      <th scope="col"><button>Price</button></th>
    </tr>
  </thead>
  <tbody>
    {offers.map(offer => 
         <tr>
        <td>{offer.title}</td>
        <td>{offer.description}</td>
        <td>{offer.price}</td>
      </tr>
        )}
    
  </tbody>
</table>
  )
}

export default HappyComponent
