import React, { useEffect, useState } from "react";
import SpecialComponent from "./SpecialComponent";
import './Special.css'

const Special = () => {
  const [specials, setSpecials] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3004/specials")
      .then((response) => response.json())
      .then((data) => {
        // Handle the response data
        console.log(data);
        setSpecials(data);
      });
  }, []);

  const [sortedSpecials, setSortedSpecials] = useState([...specials]);

  const handleSortClick = () => {
    const sortedArray = [...specials].sort((a, b) => (a.title > b.title ? 1 : -1));
    setSortedSpecials(sortedArray);
  };


  return (
    <div className="special">
        <button type="button" className="btn" onClick={handleSortClick}>
        Filter based on name
      </button>
      <SpecialComponent specials={specials} />
    </div>
  );
};

export default Special;
