import React, { useState } from 'react';

const SpecialComponent = ({ specials }) => {
  const [sortedSpecials, setSortedSpecials] = useState([...specials]);

  const handleSortClick = () => {
    const sortedArray = [...specials].sort((a, b) => (a.title > b.title ? 1 : -1));
    setSortedSpecials(sortedArray);
  };

  return (
    <div className="specialCard shadow-lg">
      <button type="button" className="btn" onClick={handleSortClick}>
        Filter based on name
      </button>
      {sortedSpecials.map((special) => (
        <div key={special.id}>
          <h4 data-toggle="tooltip" data-placement="top" title={special.title}>
            {special.title}
          </h4>
          <p>{special.description}</p>
        </div>
      ))}
    </div>
  );
};

export default SpecialComponent;
